const v_to_negaposi = (v: number) => {
  if (v >= 1) {
    return "url(#positive)";
  } else if (v < 0) {
    return "url(#negative)";
  } else if (v == 0) {
    return "url(#neutral)";
  }
  return "url(#error)";
};

const v_to_jalabel_path = (v: number) => {
  if (v >= 1) {
    return JA_LABEL_PATH["Shonin"];
  } else if (v < 0) {
    return JA_LABEL_PATH["Hitei"];
  } else if (v == 0) {
    return JA_LABEL_PATH["Churitsu"];
  }
  return JA_LABEL_PATH["Ijo"];
};

